  let temperature = Number(temp).toFixed(2);

  updateTemperatureBox(temperature);

  updateTemperatureGauge(temperature);

  // Update Temperature Line Chart
  updateCharts(
    temperatureHistoryDiv,
    newTempXArray,
    newTempYArray,
    temperature
  );
}


