.bulb-container {
  display: flex;
  align-items: center;
}

.bulb {
  width: 80px;
  height: 80px;
  background-color: #ccc;
  border-radius: 50%;
  margin-right: 10px;
  transition: background-color 0.3s ease;
}

.toggle-checkbox {
  display: none;
}

.toggle-label {
  display: block;
  position: relative;
  width: 50px;
  height: 26px;
  background-color: #ccc;
  border-radius: 26px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.toggle-label:before {
  content: "";
  position: absolute;
  top: 2px;
  left: 2px;
  width: 22px;
  height: 22px;
  background-color: white;
  border-radius: 50%;
  transition: transform 0.3s ease;
}

.toggle-checkbox:checked + .toggle-label {
  background-color: #4CAF50;
}

.toggle-checkbox:checked + .toggle-label:before {
  transform: translateX(24px);
}

.bulb-container .toggle-checkbox:checked ~ #bulb {
  background-color: yellow;
}

